import React from "react";
import { Drawer, Descriptions, Row } from "antd";

const HcpDetailDrawer = ({ data, onClose, open }) => {
  const getStucturedItems = (data) => {
    const itemList = [
      {
        key: "1",
        label: "Name",
        children: data?.name,
      },
      {
        key: "2",
        label: "Status",
        children: data?.status,
      },
      {
        key: "3",
        label: "Address",
        children: data?.address_link,
        render: (record) => {
          return Object.entries(record).map(([key, value]) => (
            <Row>{`${key}: ${value}`}</Row>
          ));
        },
      },
      {
        key: "4",
        label: "Country Code",
        children: data?.countryIsoCode,
      },
    ];
    return itemList;
  };

  return (
    <Drawer title="HCP Info" onClose={onClose} open={open} width={640}>
      <Descriptions
        title={`HCP - ${data?.name}`}
        items={getStucturedItems(data)}
      />
      {data?.affiliations &&
        Object.entries(data.affiliations).map((item) => {
          const itemData = item["child_link"];
          return (
            <Descriptions
              title={`${item?.type} - ${itemData?.name}`}
              items={getStucturedItems(itemData)}
            />
          );
        })}
    </Drawer>
  );
};

export default HcpDetailDrawer;
