import React from "react";
import { Drawer, Descriptions, Row } from "antd";

const HcpDetailDrawer = ({ data, onClose, open }) => {
  const hcpItems = [
    {
      key: "1",
      label: "Name",
      children: data?.name,
    },
    {
      key: "2",
      label: "Status",
      children: data?.status,
    },
    {
      key: "3",
      label: "Address",
      children: data?.address_link,
      render: (record) => {
        return Object.entries(record).map(([key, value]) => (
          <Row>{`${key}: ${value}`}</Row>
        ));
      },
    },
    {
      key: "4",
      label: "Country Code",
      children: data?.countryIsoCode,
    },
  ];

  const affiliatedItems = [
    {
      key: "1",
      label: "Name",
      children: data?.name,
    },
    {
      key: "2",
      label: "Status",
      children: data?.status,
    },
    {
      key: "3",
      label: "Address",
      children: data?.address_link,
      render: (record) => {
        return Object.entries(record).map(([key, value]) => (
          <Row>{`${key}: ${value}`}</Row>
        ));
      },
    },
    {
      key: "4",
      label: "Country Code",
      children: data?.countryIsoCode,
    },
  ];

  return (
    <Drawer title="HCP Info" onClose={onClose} open={open} width={640}>
      <Descriptions title={`HCP - ${data?.name}`} items={hcpItems} />
    </Drawer>
  );
};

export default HcpDetailDrawer;
