import React from "react";
import { Table, Row, Input, Space } from "antd";
const { Search } = Input;

const columns = [
  {
    title: "ID",
    dataIndex: "id",
    key: "id",
  },
  {
    title: "Name",
    dataIndex: "name",
    key: "name",
  },
  {
    title: "Status",
    dataIndex: "status",
    key: "status",
  },
  {
    title: "Address",
    dataIndex: "address_link",
    key: "address",
    render: (text) => {
      return (
        <>
          <Row>{text["addr1"]}</Row>
          <Row>{text["city"]}</Row>
        </>
      );
    },
  },
  {
    title: "Country Code",
    dataIndex: "countryIsoCode",
    key: "countryCode",
  },
];

const HcpTable = ({ data, loading, onSelectRow, onSearch }) => (
  <Space direction="vertical" style={{ width: "80%" }}>
    <Search
      placeholder="Search by HCP name"
      onSearch={onSearch}
      style={{
        width: 200,
      }}
    />
    <Table
      style={{ width: "100%" }}
      loading={loading}
      columns={columns}
      dataSource={data}
      title={() => "Health Care Providers"}
      onRow={(record) => {
        return {
          onClick: () => {
            onSelectRow(record);
          },
        };
      }}
      rowKey="id"
      bordered
    />
  </Space>
);

export default HcpTable;
